import React, { useEffect, useState } from 'react';
import './Dashboard.css';
import axios from 'axios';
//import Modal from './Modal'; // Import a Modal component
const API_BASE = "http://localhost:3001";

function Dashboard() {
	const [devices, setDevices] = useState([]);
	const [popupActive, setPopupActive] = useState(false);
	const [newDevice, setNewDevice] = useState("");

	useEffect(() => {
		GetDevices();
	}, []);

	const GetDevices = async () => {
		fetch(API_BASE + "/devices");
	}

	return (
		<div className="dashboard">
			<h4>Device Dashboard</h4>
			<ul className="device-list">
				{devices.map(device => (
					<li key={device.id}>
						ID: {device.id}, Type: {device.type}, Status: {device.status ? 'On' : 'Off'}, Live Consumption in kWh: {device.liveConsumption}, Live Consumption in SAR: {device.liveConsumption * 0.18}, Cumulative Consumption: {device.cumulativeConsumption * 0.18}
					</li>
				))}
			</ul>
		</div>
	);
}

export default Dashboard;
