import React, { useEffect, useState } from 'react';
import './Dashboard.css';
import axios from 'axios';
//import Modal from './Modal'; // Import a Modal component

function Dashboard() {
  const [devices, setDevices] = useState([]);

  useEffect(() => {
    async function fetchDevices() {
      try {
        const response = await axios.get('/api/device');
        setDevices(response.data);
      } catch (error) {
        console.error('Error fetching devices:', error);
      }
    }

    fetchDevices();
  }, []);

  return (
    <div className="dashboard">
      <h2>Device Dashboard</h2>
      <ul className="device-list">
        {devices.map(device => (
          <li key={device.id}>
            ID: {device.id}, Type: {device.type}, Status: {device.status ? 'On' : 'Off'}, Live Consumption in kWh: {device.liveConsumption}, Live Consumption in SAR: {device.liveConsumption * 0.18}, Cumulative Consumption: {device.cumulativeConsumption * 0.18}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Dashboard;
