import React, { useEffect, useState } from 'react';
import './Dashboard.css';
import axios from 'axios';
//import Modal from './Modal'; // Import a Modal component
const API_BASE = "http://localhost:3001";

function Dashboard() {
	const [devices, setDevices] = useState([]);
	const [popupActive, setPopupActive] = useState(false);
	const [newDevice, setNewDevice] = useState("");

	useEffect(() => {
		GetDevices();
	}, []);

	const GetDevices = async () => {
		fetch(API_BASE + "/devices")
			.then(res => res.json())
			.then(data => setDevices(data))
			.catch(err => console.error("Error", err));
	}

	return (
		<div className="dashboard">
			<h4>Device Dashboard</h4>
				{devices.map(device => (
					<dev className="device" key={device._id}>
						<div className="Device Id">{device.deviceId}</div>

						<div className="Type">{device.deviceType}</div>

						<div className="Location">{device.location}</div>

						<div className="Status">{device.status ? "On" : "Off"}</div>

						<div className="Live Consumption in kWh">{device.liveConsumption}</div>

						<div className="Live Consumption in SAR">{device.liveConsumption * 0.18}</div>

						<div className="Cumulative Consumption in kWh">{device.deviceId}</div>

						<div className="Cumulative Consumption in SAR">{device.deviceId * 0.18}</div>
					</dev>
				))}
		</div>
	);
}

export default Dashboard;
