import React from 'react';

function DeviceComponent({ device, flipStatus, isExpanded, toggleExpansion }) {
    return (
        <div className="device" onClick={toggleExpansion}>
            <div className="device-summary">
                <div className="Type">{device.deviceType}</div>
                <div className="Location">{device.location}</div>
            </div>

            {isExpanded && (
                <div className="device-details">
                    <div className="Device Id">{device.deviceId}</div>
                    <div className="Status">{device.status ? "On" : "Off"}</div>
                    <div className="Live Consumption in kWh">{device.liveConsumption}</div>
                    <div className="Live Consumption in SAR">{device.liveConsumption * 0.18}</div>
                    <div className="Cumulative Consumption in kWh">{device.cumulativeConsumption}</div>
                    <div className="Cumulative Consumption in SAR">{device.cumulativeConsumption * 0.18}</div>
                    <div className={`status-slider ${device.status ? 'on' : 'off'}`} onClick={(e) => { e.stopPropagation(); flipStatus(device._id); }}>
                        <div className="slider-thumb"></div>
                    </div>
                </div>
            )}
        </div>
    );
}

export default DeviceComponent;
