import React, { useState } from 'react';

function ScheduleComponent({ schedule, deleteSchedule }) {
    const [isExpanded, setIsExpanded] = useState(false);

    return (
        <div className="schedule" onClick={() => setIsExpanded(!isExpanded)}>
            <div className="schedule-summary">
                <div>Device ID: {schedule.deviceId}</div>
            </div>

            {isExpanded && (
                <div className="schedule-details">
                    <div>Start Time: {schedule.startTime}</div>
                    <div>End Time: {schedule.endTime}</div>
                    <button onClick={() => deleteSchedule(schedule._id)}>Delete Schedule</button>
                </div>
            )}
        </div>
    );
}

export default ScheduleComponent;
