import React, { useEffect, useState } from 'react';
import './Dashboard.css';
import axios from 'axios';
import DeviceComponent from './DeviceComponent';

const API_BASE = "http://localhost:3001";

function Dashboard() {
    const [devices, setDevices] = useState([]);
    const [newDevice, setNewDevice] = useState({ deviceId: '', deviceType: '', location: '' });
    const [expandedDeviceId, setExpandedDeviceId] = useState(null); // Track the expanded device

    useEffect(() => {
        GetDevices();
    }, []);

    const GetDevices = async () => {
        const response = await axios.get(`${API_BASE}/devices`);
        setDevices(response.data);
    }

    const flipStatus = async (id) => {
        await axios.put(`${API_BASE}/flip-status/${id}`);
        GetDevices(); // Refresh the list of devices
    };

    const handleAddDevice = async (event) => {
        event.preventDefault();
        await axios.post(`${API_BASE}/device`, newDevice);
        setNewDevice({ deviceId: '', deviceType: '', location: '' });
        GetDevices();
    };

    const toggleExpansion = (id) => {
        setExpandedDeviceId(id === expandedDeviceId ? null : id); // Toggle expanded device
    };

    return (
        <div className="dashboard">
            <h4>Device Dashboard</h4>
            {/* Add new device form */}
            {/* ... form code ... */}

            {devices.map(device => (
                <DeviceComponent 
                    key={device._id} 
                    device={device} 
                    flipStatus={flipStatus} 
                    isExpanded={device._id === expandedDeviceId}
                    toggleExpansion={() => toggleExpansion(device._id)}
                />
            ))}
        </div>
    );
}

export default Dashboard;
